CREATE PROCEDURE sp_GetOrdersByDateRange
    @startDate DATETIME,
    @endDate DATETIME
AS
BEGIN
	BEGIN TRAN
	BEGIN TRY
		SELECT *
		FROM Orders
		WHERE OrderDate BETWEEN @startDate AND @endDate 
			OR OrderDate = @startDate 
			OR OrderDate = @endDate
		COMMIT TRAN;
	END TRY
	BEGIN CATCH
		ROLLBACK TRAN
			DECLARE @Notification VARCHAR(20)=ERROR_MESSAGE();
		THROW 1,@Notification,16;
	END CATCH
END
GO
